function getLocalTimeStringFromMinute(minute) {
    if (minute == Infinity)
        return "00:00";
    let pad = (d) => (d < 10) ? '0' + d.toString() : d.toString();
    let t1 = getDateFromMinute(minute);
    return `${pad(t1.getHours())}:${pad(t1.getMinutes())}`;
}
